mapper.update:
UPDATE orders
car_id = #{carId}, start_site = #{startSite}, end_site = #{endSite}, addresser_name = #{addresserName}, addresser_phone = #{addresserPhone}, addresser_card = #{addresserCard}, recipients_name = #{recipientsName}, recipients__phone = #{recipientsPhone}, recipients__card = #{recipientsCard}, delivery_time = #{deliveryTime}, receipt_time = #{receiptTime}, status = #{status}, remark = #{remark}, is_deleted = #{isDeleted}, creator_id = #{creatorId}, modifier_id = #{modifierId}, create_time = #{createTime}, modify_time = #{modifyTime},
WHERE id=#{id}

mapper.select:
SELECT *
FROM orders
WHERE (is_deleted = 0

	AND order_no LIKE CONCAT('%', #{orderNo}, '%')


	AND addresser_name LIKE CONCAT('%', #{addresserName}, '%')


	AND status = #{status}


	AND creator_id = #{creatorId}

)

	(OR end_site LIKE CONCAT('%', #{endSite}, '%')
