mapper.update:
update sys_cars
member_id=#{memberId}, licence_plate=#{licencePlate}, car_type=#{carType}, status=#{status}, remark=#{remark}, is_deleted=#{isDeleted}, creator_id=#{creatorId}, modifier_id=#{modifierId}, create_time=#{createTime}, modify_time=#{modifyTime}
id=#{id}

mapper.select:
	  	select cars.id,cars.licence_plate, cars.car_type,cars.status,cars.remark,
	cars.is_deleted,cars.creator_id,cars.modifier_id,cars.create_time,cars.modify_time,m.id mid,m.realname
	from sys_cars cars 
	left join member m
	on cars.member_id=m.id
  	
  		
  		
  			cars.id=#{id}
  		
  		
  		
  			and cars.licence_plate=#{licencePlate}
  		
  		
  		
  			and cars.car_type=#{carType}
  		
  		
  		
  			and cars.member_id=#{memberId}
